import React from 'react';
import { useState } from 'react';
import styled from 'styled-components';
import ListPage from './Listpage';
import FilterComponent from './FilterComponent';
import { Link } from 'react-router-dom';
import modalStyles from './ModalStyle'; // 스타일 파일 import

const Header = styled.header`
background-color: #488AEE;
// padding: 5px;
height: 130px;
text-align: center;
justify-content: center;
`;

const Logoimg = styled.img`
// padding: 10px;
height: 110px;
text-align: center;
display: inline-block;
cursor: pointer; /* 커서를 포인터로 변경하여 클릭 가능하게 함 */
`;

const Linkdiv = styled.div`
background-color: #488AEE;
text-decoration: none;
color: inherit;
text-align: right;
transition: background-color 0.3s ease-in-out;
`;

const StyledButton = styled.button`
text-decoration: none;
color: inherit;
background-color: lightGray;
padding: 5px 10px;
margin-right: 15px;
margin-top: 10px;
border: none; /* 테두리 제거 */
border-radius: 13px;
display: inline-block;
cursor: pointer;
transition: background-color 0.3s ease-in-out;

font-size: 13px; /* 글자 크기 지정 */
font-weight: 400; /* 글자 두께 지정 */
`;

const Headerlist = styled.div`
background-color: #488AEE;
text-align: right;
padding-bottom: 5px;
padding-right: 10px;
`;

const MyLink = styled(Link)`
text-decoration: none;
color: inherit;
padding: 5px 10px;
margin-left: 5px;
display: inline-block;
transition: background-color 0.3s ease-in-out;

font-size: 13px; /* 글자 크기 지정 */
font-weight: 400; /* 글자 두께 지정 */

`;

const Title = styled.h2`
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  left: -500px;
  margin-top:40px;
`;

const Dropdown = styled.div`
  diplay:flex;
  position:absolute;
  border: 3px solid #ccc;
  border-radius: 10px;
  padding: 8px;
  cursor: pointer;
  width: 100px;
  text-align: center;
  left:20px;
`;

const DropdownMenu = styled.div`
  display: ${(props) => (props.isOpen ? 'block' : 'none')};
  position: absolute;
  left:30px;
  background-color: #fff;
  border: 3px solid #ccc;
  border-radius: 10px;
  width: 100px;
  z-index: 1;
`;

const DropdownItem = styled.div`
  padding: 5px;
  cursor: pointer;
`;

//리스트 container
const Container = styled.div`
  position: relative;
  height: 70vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;

const RoundedBox = styled.div`
  border-radius: 80px;
  border: 3px solid #ccc;
  padding: 10px;
  margin-right: 40px;
  margin-top:100px;
  width: 270px;
  height: 350px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;

`;

const PaginationContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin-top: auto; /* 페이지네이션을 컨테이너의 아래로 밀어냅니다. */
  margin-bottom: 20px; 
`;



const PartyAllList= () => {

    const [partiesData] = useState([
        {
            "partyPk": 8,
            "festPk": 83,
            "userPk": 8,
            "partyRecruitLimit": 4,
            "partyRecruitCurr": 1,
            "partyChatUrl": "",
            "partyStart": "2023-09-30",
            "partyEnd": "0001-01-01",
            "partyDetail": "det1",
            "partyState": "모집",
            "partyTag": "tag1"
            },
            {
            "partyPk": 9,
            "festPk": 99,
            "userPk": 27,
            "partyRecruitLimit": 2,
            "partyRecruitCurr": 2,
            "partyChatUrl": "",
            "partyStart": "2023-09-30",
            "partyEnd": "0002-02-02",
            "partyDetail": "det2",
            "partyState": "마감",
            "partyTag": "tag2"
            },
            {
            "partyPk": 11,
            "festPk": 23,
            "userPk": 49,
            "partyRecruitLimit": 2,
            "partyRecruitCurr": 1,
            "partyChatUrl": "dfd",
            "partyStart": "2023-10-04",
            "partyEnd": "2023-10-09",
            "partyDetail": "tjfaud",
            "partyState": "모집",
            "partyTag": "xorm"
            },
            {
            "partyPk": 12,
            "festPk": 94,
            "userPk": 18,
            "partyRecruitLimit": 2,
            "partyRecruitCurr": 1,
            "partyChatUrl": "",
            "partyStart": "2023-10-30",
            "partyEnd": "0001-01-01",
            "partyDetail": "파아티설명",
            "partyState": "모집",
            "partyTag": "태에그"
            },
            {
            "partyPk": 13,
            "festPk": 23,
            "userPk": 48,
            "partyRecruitLimit": 2,
            "partyRecruitCurr": 1,
            "partyChatUrl": "",
            "partyStart": "2023-10-30",
            "partyEnd": "0001-01-01",
            "partyDetail": "파아티설명",
            "partyState": "모집",
            "partyTag": "태에그"
            },
            {
            "partyPk": 14,
            "festPk": 64,
            "userPk": 91,
            "partyRecruitLimit": 4,
            "partyRecruitCurr": 1,
            "partyChatUrl": "asdf",
            "partyStart": "2023-10-31",
            "partyEnd": "2023-09-30",
            "partyDetail": "qwer",
            "partyState": "모집",
            "partyTag": "zxcv"
            },
        // 다른 파티 정보들도 추가할 수 있어요.
        // { id: 2, title: '...', location: '...', text: '...', date: '...' },
        // { id: 3, title: '...', location: '...', text: '...', date: '...' },
        // ...
      ]);

      //페이지네이션 기능
      const partiesPerPage = 4;
      const [currentPage, setCurrentPage] = useState(1);
    
      const indexOfLastParty = currentPage * partiesPerPage;
      const indexOfFirstParty = indexOfLastParty - partiesPerPage;
      const currentParties = partiesData.slice(indexOfFirstParty, indexOfLastParty);
    
      const handlePageChange = (pageNumber) => {
        setCurrentPage(pageNumber);
      };
    
      const handlePrevPage = () => {
        if (currentPage > 1) {
          setCurrentPage(currentPage - 1);
        }
      };
    
      const handleNextPage = () => {
        const totalPages = Math.ceil(partiesData.length / partiesPerPage);
        if (currentPage < totalPages) {
          setCurrentPage(currentPage + 1);
        }
      };

      // 드롭다운 기능
      const [isOpen, setIsOpen] = useState(false);
      const [selectedLocation, setSelectedLocation] = useState('지역');
    
      const locations = ['남구', '수영구', '진구', '동래구','북구'];
    
      const handleToggleDropdown = () => {
        setIsOpen(!isOpen);
      };
    
      const handleLocationSelect = (location) => {
        setSelectedLocation(location);
        setIsOpen(false);
      };
    return (
    <div>

        <div>
            <Linkdiv>
                <StyledButton>로그아웃</StyledButton>
                </Linkdiv>
            <Header>
            <Link to="/."> {/* 메인화면으로 이동 */}
                <Logoimg src="info.png" alt="Logo" />
            </Link>
                <Headerlist>
                
                <MyLink to ="/festival-list">축제 목록</MyLink>
                <MyLink to ="/partyall-list">전체 파티 목록</MyLink>
                <MyLink to ="/my-party-list">내 파티</MyLink>
                </Headerlist>
            </Header>
        <Title>전체 파티 목록</Title>

                <Dropdown onClick={handleToggleDropdown}>
                {selectedLocation}
                <DropdownMenu isOpen={isOpen}>
                {locations.map((location) => (
                    <DropdownItem key={location} onClick={() => handleLocationSelect(location)}>
                    {location}
                    </DropdownItem>
                ))}
                </DropdownMenu>
            </Dropdown>

        <Container>
            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
             {currentParties.map((party) => (
             <RoundedBox key={party.partyPk}>
                    <p styled={{ margin:'0',fontSize:'200px'}}>{party.festPk}</p>
                    <p>{party.location}</p>
                    <hr />
                    <p>{party.partyDetail}</p>
                    <p>{party.partyStart}</p>
            </RoundedBox>
                ))}
                </div>

                <PaginationContainer>
                    <button onClick={handlePrevPage}>{'< '}</button>
                    {Array.from({ length: Math.ceil(partiesData.length / partiesPerPage) }, (_, index) => (
                    <button key={index + 1} onClick={() => handlePageChange(index + 1)}>
                        {index + 1}
                    </button>
                    ))}
                    <button onClick={handleNextPage}>{' >'}</button>
                </PaginationContainer>
                
                </Container>
            </div>
    </div>
    
    );
};

export default PartyAllList;
