import React, { useState } from 'react';
import { Navigate } from 'react-router';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';
import qs from "qs";




const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: #488aee; /* Î∞∞Í≤ΩÏÉâ ÏÑ§Ï†ï */
  padding: 20px; /* Ï†ÅÏ†àÌïú Ìå®Îî© Ï∂îÍ∞Ä */
  min-height: 95vh;
`;

const LogoImage = styled.img`
  width: 300px; /* ÌÅ∞ Î°úÍ≥† ÌÅ¨Í∏∞ */
  height: auto;
  margin-top: 0px;
  margin-bottom: 0px;
`;

const InputContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-bottom: 20px;
`;

const Input = styled.input`
  width: 300px;
  height: 40px;
  margin-bottom: 10px;
  padding: 10px;
  border-radius: 200px;
`;

const ImagesContainer = styled.div`
  display: flex;
  justify-content: space-between;
`;

const HiImage = styled.img`
  width: 250px; /* Îçî ÌÅ∞ Ïù¥ÎØ∏ÏßÄ ÌÅ¨Í∏∞ */
  height: auto;
`;

const ExcitedImage = styled.img`
  width: auto; /* Îçî ÌÅ∞ Ïù¥ÎØ∏ÏßÄ ÌÅ¨Í∏∞ */
  height: 150px;
`;
const RoundedBox = styled.div`
  border-radius: 200px;
  width:700px;
  padding: 70px;
  background-color: #91B9F5; /* Î∞∞Í≤ΩÏÉâ ÏÑ§Ï†ï */
  box-shadow: 0px 0px 15px 0px rgba(0,0,0,0.2); /* Í∑∏Î¶ºÏûê Ìö®Í≥º */
  margin-top: 50px;
`;
const LoginButton = styled.button`
display: block;
width: 200px; /* Î≤ÑÌäºÏù¥ Ïª®ÌÖåÏù¥ÎÑàÏùò Í∞ÄÎ°ú Í∏∏Ïù¥Î•º Í∞ÄÏßÄÎèÑÎ°ù Ìï® */
background-color: #0066ff;
color: white;
border: none;
border-radius: 200px;
padding: 10px 20px;
font-size: 16px;
cursor: pointer;
`;

const SignupContainer = styled.div`
  text-align: center;
  margin-top: 20px;
`;

const SignupLine = styled.div`
  width: 100%;
  border-top: 1px solid white;
  margin: 20px 0;
`;

const SignupText = styled.p`
  color: black;
  font-size: 20px;
  margin: 0;
`;

const SignupButton = styled.button`
  background-color: #0066ff;
  color: white;
  border: none;
  border-radius: 200px;
  padding: 10px 20px;
  font-size: 16px;
  cursor: pointer;
`;


const logobuife = '/info.png';
const hiImage = '/Î∞±Í≤ΩÏù¥(hi).png';
const excitedImage = '/ÎøåÍ≥µÏù¥(Ïã†ÎÇ®).png';


const LoginForm=()=> {
  const [userID, setuserID] = useState('');
  const [userPW, setuserPW] = useState('');
  const handleLogin = async () => {
    try {

      
      const params = new URLSearchParams();
      params.append('userID', userID);
      params.append('userPW', userPW);

      const requestOptions = {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
 
        },
        body: params,
      };
    
  

      const response = await fetch('https://port-0-server-cloudtype-4fju66f2clmyxbee6.sel5.cloudtype.app/members/login', requestOptions);
      
      if (!response.ok) {
        throw new Error('ÏÑúÎ≤Ñ ÏùëÎãµÏù¥ Ïã§Ìå®ÌñàÏäµÎãàÎã§.');
      }

      const data = await response.text();
      //userIDÎ•º ÌôïÏù∏ÌïòÍ∏∞ ÏúÑÌïú alert
      //alert(requestOptions.data.userID + "Îãò, ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Î°úÍ∑∏Ïù∏ ÎêòÏóàÏäµÎãàÎã§ üîê");
      //window.location.href = '/MyPage'; // URL Î≥ÄÍ≤Ω // Î°úÍ∑∏Ïù∏Ïù¥ ÏÑ±Í≥µÌñàÏùÑ ÎïåÏùò Î°úÏßÅ
       console.log(data); // ÏÑúÎ≤ÑÏóêÏÑú Î∞õÏùÄ Îç∞Ïù¥ÌÑ∞ ÌôïÏù∏
       //ÏÑúÎ≤ÑÏóêÏÑú Ï†ÑÎã¨Îêú ÌÜ†ÌÅ∞
       const token=data.token;
       //ÌÜ†ÌÅ∞ÏùÑ Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóê Ï†ÄÏû•
       localStorage.setItem('token',token);
       localStorage.setItem("bbs_access_token", requestOptions.data.token);
				localStorage.setItem("id", requestOptions.data.userID);

       //Î°úÍ∑∏Ïù∏Ïù¥ ÏÑ±Í≥µÌïòÎ©¥ Îã§ÏùåÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô 
    } catch (error) {
      console.error('Î°úÍ∑∏Ïù∏ Ïã§Ìå®:', error);
      alert('ÏïÑÏù¥Îîî ÎòêÎäî Ìå®Ïä§ÏõåÎìúÎ•º ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî!');
      // Î°úÍ∑∏Ïù∏ Ïã§Ìå® ÏãúÏùò Ï≤òÎ¶¨ Î°úÏßÅÏùÑ Ï∂îÍ∞ÄÌïòÏÑ∏Ïöî
    }
  };


  /*const handleLogin = async()=>{
    try {
      const response = await axios.post('https://port-0-buife-5mk12alp6foaqx.sel5.cloudtype.app/members/login', qs.stringify({
        userID: userID,
        userPW: userPW,
      }), {
        method: 'POST',
        headers: {
          "Content-Type": "application/x-www-form-urlencoded",	// Default
        },})
        console.log(response.data);
    } catch (error) {
      // Î°úÍ∑∏Ïù∏ Ïã§Ìå® Ïãú ÏóêÎü¨ Ï≤òÎ¶¨
      console.error('Î°úÍ∑∏Ïù∏ Ïã§Ìå®:', error.message);
    }
    
  };*/
  

  /*const handleLogin = async () => {
    try {
      const response = await axios.post('https://port-0-buife-5mk12alp6foaqx.sel5.cloudtype.app/members/login', {
        userID: userID,
        userPW: userPW,
      });

      // ÏÑúÎ≤ÑÏóêÏÑú ÏùëÎãµ Ï≤òÎ¶¨
      console.log(response.data); // ÏÑúÎ≤ÑÏóêÏÑú Î∞õÏùÄ Îç∞Ïù¥ÌÑ∞ ÌôïÏù∏
      // ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Î°úÍ∑∏Ïù∏ÎêòÏóàÏùåÏùÑ Ï≤òÎ¶¨ÌïòÎäî Î°úÏßÅ Ï∂îÍ∞Ä
    } catch (error) {
      // Î°úÍ∑∏Ïù∏ Ïã§Ìå® Ïãú ÏóêÎü¨ Ï≤òÎ¶¨
      console.error('Î°úÍ∑∏Ïù∏ Ïã§Ìå®:', error.message);
    }
  };*/
  
 return (
   <div className="App">
     <div className="mainpage">
       
       <div className="mainbox">
        <Container>
          <RoundedBox>
          <Link to="/.">
            <LogoImage src={logobuife} alt="Buife Î°úÍ≥†" />
          </Link>
        
            <InputContainer>
              <Input type="text" placeholder="Ïù¥Î©îÏùº" onChange={(e) => setuserID(e.target.value)}/>
              <Input type="password" placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏" onChange={(e) => setuserPW(e.target.value)}/>

  
            <LoginButton type="button" onClick={handleLogin}
            className="loginButton">Î°úÍ∑∏Ïù∏
            </LoginButton>
            </InputContainer>
            
            <SignupLine />
          
            <SignupContainer>
              <SignupText>ÏïÑÏßÅ BUIFE ÌöåÏõêÏù¥ ÏïÑÎãàÏã†Í∞ÄÏöî?</SignupText>
                <Link to="/signup2"><SignupButton>ÌöåÏõêÍ∞ÄÏûÖ</SignupButton></Link>
                
            </SignupContainer>


            <ImagesContainer>
            <ExcitedImage src={excitedImage} alt="ÎøåÍ≥µÏù¥" />
            <HiImage src={hiImage} alt="Î∞±Í≤ΩÏù¥" />
            
            </ImagesContainer>

              
          </RoundedBox>
         </Container>
       </div>
     </div>
   </div>
 );
}



export default LoginForm;
