import { Route, Routes } from 'react-router-dom';
import { useState } from 'react';
import Mypage from './pages/Mypage';
import Mainpage from './pages/Mainpage';
import FestivalList from './pages/festival-list';
import PartyList from './pages/party-list';
import LoginForm from './pages/LoginForm'
import Logout from './pages/Logout';
import SignUp from './pages/SignUp';

import React from 'react';
import './App.css';
import FestivalDetail from './pages/festival-detail';



function App() {

  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = (username, password) => {
    // 실제 로그인 처리 및 토큰 발급 등을 수행하는 부분
    // 토큰을 저장하고 로그인 상태를 변경
    // 예시로, 토큰 저장 및 간단한 상태 업데이트만 수행
    localStorage.setItem('token', 'yourAuthTokenHere');
    setIsLoggedIn(true);
  };

  const handleSignUp = (username, password) => {
    // 회원가입 처리 및 토큰 발급 등을 수행하는 부분
    // 토큰을 저장하고 로그인 상태를 변경
    // 예시로, 토큰 저장 및 간단한 상태 업데이트만 수행
    localStorage.setItem('token', 'yourAuthTokenHere');
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    // 로그아웃 처리 및 토큰 만료 등을 수행하는 부분
    // 토큰 제거 및 로그인 상태 변경
    // 예시로, 토큰 제거 및 간단한 로그아웃 상태 업데이트만 수행
    localStorage.removeItem('token');
    setIsLoggedIn(false);
  };


  return (
    <Routes>
      <Route path="/" element={<Mainpage />} />
      
      <Route path="/Mypage" element={<Mypage/>} />
      <Route path="/festival-list" element={<FestivalList />} />
      <Route path="/festival-detail" element={<FestivalDetail />} />
      <Route path="/party-list" element={<PartyList/>} />
      <Route path="/LoginForm" element={<LoginForm/>} />
      <Route path="/Logout" element={<Logout/>} />
      <Route path="/SignUp" element={<SignUp/>} />
    </Routes>
  );

}

export default App;
