const modalStyles = {

    // 추가적인 스타일은 이곳에 추가 가능합니다.
    writeButton: {
      padding: '10px 20px',
      fontSize: '16px',
      backgroundColor: '#007bff',
      color: '#fff',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
      marginTop: '10px',
      display:'flex',
      justifyContent: 'flex-end',
      alignItems: 'center',
  
      paddingRight: '20px', // 오른쪽 여백
    },
    writeButtonHover: {
      backgroundColor: '#0056b3',
    },
  };
  
  export default modalStyles;

    //PartyInfo.js에서 글쓰기 버튼에 적용되는 style 코드이다
